import React from 'react';

//import icons
import {HiChartBar, HiUser, HiGlobe} from 'react-icons/hi'

const Stats = () => {
  return <section>
    <div className='container mx-auto'> 
      <div className='flex flex-col gap-y-6 lg:flex-row lg:justify-between'>
        {/*item*/}
        <div className='flex item-center gap-x-6'>
          {/*item icon*/}
          <div className='bg-white/10 w-20 h-20 rounded-full flex items-center justify-center
              text-blue text-2xl lg;text-4xl'>
            <HiChartBar/>
          </div>
          {/*item text*/}
          <div className='text-2xl font-bold lg:text-[40px] lg:mb-2'>
            $30B
            <div className='text-3xl text-gray'>Digital Currency Exchange</div>
          </div>
        </div>

        {/*item*/}
        <div className='flex item-center gap-x-6'>
          {/*item icon*/}
          <div className='bg-white/10 w-20 h-20 rounded-full flex items-center justify-center
              text-blue text-2xl lg;text-4xl'>
            <HiUser/>
          </div>
          {/*item text*/}
          <div className='text-2xl font-bold lg:text-[40px] lg:mb-2'>10M+</div>
          <div className='text-gray'>Trusted Wallet Investors</div>
        </div>

        {/*item*/}
        <div className='flex item-center gap-x-6'>
          {/*item icon*/}
          <div className='bg-white/10 w-20 h-20 rounded-full flex items-center justify-center
              text-blue text-2xl lg;text-4xl'>
            <HiGlobe/>
          </div>
          {/*item text*/}
          <div className='text-2xl font-bold lg:text-[40px] lg:mb-2'>$126</div>
          <div className='text-gray'>Countries Supported</div>
        </div>
      </div>
    </div>
  </section>;
};

export default Stats;
