import React from 'react';

//import Image
import Image2 from '../assets/img/feature-2-img.png'

const FeaturesSection2 = () => {
  return (
  <section className='py-[30px] lg:py-[120px]'>
    <div className='container mx-auto'>
      <div className='flex flex-col justify-end items-center lg:flex-row'>
        {/*Image */}
        <div className='flex-1 lg:absolute lg:left-0 order-2 lg:order-1'>
          <img src={Image2}/>
        </div>
        
      </div>

      <div className='flex-1 max-w-[456]'>
        <h3>Detailed Statistics</h3>
        <p>View all mining information in real-time, at any point, at any location
          and decide which polls you want to mine in.
        </p>
        <button>Learn more</button>
      </div>

    </div>
  </section>
  );
};

export default FeaturesSection2;
