import React from 'react';

//import images
import Image from '../assets/img/why-img.png'


const Why = () => {
  return <section className='section'>
    <div className='container mx-auto'>
      <div className='flex flex-col items-center gap-x-8 lg:flex-row'>
        {/*images*/}
        <div className='order-2 lg:order-1' data-aos='fade-right' data-aos-offset='400'>
          <img src={Image} alt=''></img>
        </div>
        {/*text*/}
        <div className='order-1 lg:order-2'>
          <h2 className='section-title'>Why should you choose CRAPPO</h2>
          <p className='section-subtitle'>Experience the next generation cryptocurrency platform
            No financial borders, extra fees and fake reviews.
          </p>
          <button className='btn px-6'>Learn more</button>
        </div>
      </div>
    </div>
  </section>;
};

export default Why;
