import React from 'react';

//import data
import { currency } from '../data';

//import icons
import { IoIosArrowForward } from 'react-icons/io';

const Trade = () => {
  return <section className='section lg:pt-[320px] bg-gradient-to-be from-[#f8f9fb] to-[]'>
    <div className='container mx-auto'>
      <h2>Trade securely and market the high growth cryptocurrencies</h2>
      {/*Items */}
      <div>
        {currency.map((item, index)=>{
          //destructure items
          const {image, name, abbr, description} = item;
          return (
          <div key={index}>
            <div>
              {/*Item image */}
              <img src={image} alt=''></img>
              {/*Item name */}
              <div>
                <div>{name}</div>
                <div>{abbr}</div>
              </div>
              {/* Item description */}
              <p>{description}</p>
              {/* btn */}
              <button>Start mining</button>
            </div>
          </div>
          );
        })}
      </div>
    </div>
  </section>;
};

export default Trade;
