import React from 'react';

//import images
import Image from '../assets/img/hero-img.png';

//import icons
import {IoIosArrowDroprightCircle} from 'react-icons/io';

const Hero = () => {
  return <section>
    <div className='container mx-auto'>
      <div>
        {/*Hero text*/}
        <div>
          {/*Badge text */}
          <div className='bg-white/10 p-1 mb-6 rounded-full
          pl-1 pr-3 max-w-[365px]'>
            <div className='flex items-cem'>
              <div>75% SAVE</div>
              <div>For the Black Friday weekend</div>
            </div>
          </div>
          {/*Title */}
          <h1>Fastest & secure platform to invest in crypto</h1>
          <p>Buy ans sell cryptocurrencies, trusted by 10M wallets with over $30 billion in transactions</p>
          <button>Try for free</button>
        </div>
        {/* Hero image */}
        <div>Image</div>
      </div>
    </div>  
  </section>;
};

export default Hero;
