import React from 'react';

//import data
import { currency } from '../data';

//import icons
import { IoIosArrowForward } from 'react-icons/io';

const Trade = () => {
  return <section className='section lg:pt-[320px] bg-gradient-to-b from-[#f8f9fb] to-[#fafbff] text-darkblue
    lg:-mt-[320px]'>
    <div className='container mx-auto'>
      <h2 className='section-title text-center mb-16'>Trade securely and market the high growth cryptocurrencies</h2>
      {/*Items */}
      <div className='flex flex-col gap-[45px] lg:flex-row'>
        {currency.map((item, index)=>{
          //destructure items
          const {image, name, abbr, description} = item;
          return (
          <div className='w-full rounded-2xl py-12 px-6 shadow-primary cursor-pointer transition duration-300' key={index}>
            <div className='flex flex-col justify-center items-center'>
              {/*Item image */}
              <img className='mb-12' src={image} alt=''></img>
              {/*Item name */}
              <div className='mb-4 flex items-center gap-x-2'>
                <div className=''>{name}</div>
                <div>{abbr}</div>
              </div>
              {/* Item description */}
              <p>{description}</p>
              {/* btn */}
              <button>Start mining</button>
            </div>
          </div>
          );
        })}
      </div>
    </div>
  </section>;
};

export default Trade;
