import React from 'react';

//import images
import Image from '../assets/img/hero-img.png';

//import icons
import {IoIosArrowDroprightCircle} from 'react-icons/io';

const Hero = () => {
  return <section>
    <div className='container mx-auto'>
      <div className='flex flex-col items-center lg:flex-row'>
        {/*Hero text*/}
        <div className='flex-1'>
          {/*Badge text */}
          <div className='bg-white/10 p-1 mb-6 rounded-full
          pl-1 pr-3 max-w-[365px]' data-aos='fade-down' data-aos-delay='400'>
            <div className='flex items-cemter justify-between text-sm lg:text-base'>
              <div className='bg-white text-darkblue rounded-full font-medium py-1 px-4'>75% SAVE</div>
              <div>For the Black Friday weekend</div>
            </div>
          </div>
          {/*Title */}
          <h1 className='text-[32px] lg:text-[64px] font-bold leading-tight mb-6'>Fastest & secure platform to invest in crypto</h1>
          <p className='max-w-[440px] leading-relaxed mb-8'>Buy ans sell cryptocurrencies, trusted by 10M wallets with over $30 billion in transactions</p>
          <button className='btn gap-x-6 pl-6 text-sm lg:h-16 lg:text-base'>
            Try for free
            <IoIosArrowDroprightCircle className='text-2xl lg:text-3xl'/>
          </button>
        </div>


        {/* Hero image */}
        <div className='flex-1'>
          <img src={Image} alt=''/>
        </div>
      </div>
    </div>  
  </section>;
};

export default Hero;
